class CustomerMap extends StatefulWidget {
  var mCenterId;
  CustomerMap(this.mCenterId);

  @override
  State<StatefulWidget> createState() => _CustomerMap();
}

class _CustomerMap extends State<CustomerMap> {
  LatLng _center = null;
  CurrentLoactionBean userUsingLocation = new CurrentLoactionBean();

  var formatter = new DateFormat('dd/MMM/yyyy');
  var formatterWithHours = new DateFormat('dd/MM/yyyy HH:mm');
  GlobalKey<ScaffoldState> _scaffoldHomeState = new GlobalKey<ScaffoldState>();

  List<Marker> markers = List<Marker>();
  Completer<GoogleMapController> _controller = Completer();

  Timer _ticker;

  final Color inactiveColor = Colors.grey;
  final String activeText = 'Center';
  final String inactiveText = 'Users';
  final Color activeTextColor = Colors.white70;
  final Color inactiveTextColor = Colors.white70;

  @override
  void dispose() {
    _ticker.cancel();
    super.dispose();
  }

  @override
  void initState() {
    super.initState();
    //_ticker =  Timer.periodic(Duration(seconds: 5), (Timer t) => setStateCustom());
    //_pressDuration = "00:00:00";
    setCenterMapPointLocation();
  }

  void setStateCustom() {
    setState(() {});
  }

  setCenterMapPointLocation() async {
    SharedPreferences prefs = await SharedPreferences.getInstance();
    try {
      setState(() {
        double geoLat = prefs.get(TablesColumnFile.geoLatitude);
        double geoLong = prefs.get(TablesColumnFile.geoLongitude);
        _center = LatLng(geoLat, geoLong);
        userUsingLocation.musrcode = prefs.get(TablesColumnFile.musrcode);
        userUsingLocation.mgeolatd = geoLat.toString();
        userUsingLocation.mgeologd = geoLong.toString();
        userUsingLocation.musrname = prefs.get(TablesColumnFile.musrname);
        userUsingLocation.mreportinguser =
            prefs.get(TablesColumnFile.mreportinguser);
      });
    } catch (_) {}
  }

  @override
  Widget build(BuildContext context) {
    return new WillPopScope(
      onWillPop: () {
        return Future(() => true);
      },
      child: Scaffold(
        key: _scaffoldHomeState,
        floatingActionButton: FloatingActionButton(
          onPressed: () {
            setState(() {});
          },
          child: Icon(Icons.navigation),
          backgroundColor: Colors.blueGrey.withOpacity(0.5),
        ),
        body: _buildMap(),
        // appBar: _buildAppBar(),
      ),
    );
  }

  AppBar _buildAppBar() {
    return AppBar(
      backgroundColor: Colors.white,
      title: Text(
        "Map".toUpperCase(),
        style: TextStyle(fontSize: 19.0, color: Colors.black87),
      ),
      automaticallyImplyLeading: false,
      centerTitle: true,
    );
  }

  Widget _buildMap() {
    double height = MediaQuery.of(context).size.height;
    double width = MediaQuery.of(context).size.width;
    double mapHeight = height; //- 200.0;
    double bottomheight = (height - mapHeight) - 130;
    double bottomWidth = width - 50;
    return Column(
      children: [
        new Container(
            height: mapHeight,
            child: GoogleMap(
                markers: markers.toSet(),
                initialCameraPosition: CameraPosition(
                  target: _center,
                  zoom: 14.0,
                ),
                onMapCreated: onMapCreated,
                zoomControlsEnabled: false,
                zoomGesturesEnabled: true,
                scrollGesturesEnabled: true,
                compassEnabled: true,
                rotateGesturesEnabled: true,
                mapToolbarEnabled: true,
                tiltGesturesEnabled: true,
                gestureRecognizers: <Factory<OneSequenceGestureRecognizer>>[
                  new Factory<OneSequenceGestureRecognizer>(
                    () => new EagerGestureRecognizer(),
                  ),
                ].toSet())),
      ],
    );
  }

  void onMapCreated(GoogleMapController controller) async {
    controller.setMapStyle(MapsUtils.mapTheme);
    await getCustomerLocationsBAsedOnCenterId().then((onValue) {
      setState(() {});
    });
    ;
    _controller.complete(controller);
    setState(() {});
  }

  var infoWindowVisible = false;
  GlobalKey<State> key = new GlobalKey();

  Opacity marker(CurrentLoactionBean val) {
    return Opacity(
      child: val.profileimage != null
          ? Container(
              decoration: BoxDecoration(
                color: Colors.blueAccent,
                image: DecorationImage(
                  image: MemoryImage(File(val.profileimage).readAsBytesSync()),
                  fit: BoxFit.fill,
                ),
              ),
            )
          : Container(
              alignment: Alignment.bottomCenter,
              child: Image.asset(
                'assets/images/destination_map_marker.png',
                width: 49,
                height: 65,
              )),
      opacity: infoWindowVisible ? 0.0 : 1.0,
    );
  }

  Future<Null> getCustomerLocationsBAsedOnCenterId() async {
    getCustomers(widget.mCenterId);
  }

  Future<Null> getCustomers(String musrcode) async {
    markers.clear();

    int centerId = 0;
    try {
      centerId = int.parse(musrcode);
    } catch (_) {}

    print("centerrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrr");
    markers = List<Marker>();
    showMessage("Fetching Customers Locations...");
    GetCurrentLocationOnSuperUsers getCurrentLocationOnSuperUsers =
        new GetCurrentLocationOnSuperUsers();
    await getCurrentLocationOnSuperUsers
        .getCustomersFromMiddleWareData(centerId)
        .then((List<CustomerListBean> result) async {
      if (result != null) {
        showMessageWithoutProgress(" ${result.length} Customers Found");
        result.forEach((val) async {
          double lat = 0.0;
          double long = 0.0;
          try {
            lat = double.parse(val.mgeolatd);
            long = double.parse(val.mgeologd);
          } catch (_) {}

          CurrentLoactionBean beanValForImageOnly = new CurrentLoactionBean();

          if (val.mgender == 'M') {
            beanValForImageOnly.profileimage =
                "";
          } else if (val.mgender == 'F') {
            beanValForImageOnly.profileimage =
                "";
          }

          try {
            Marker marker = Marker(
                markerId: MarkerId(val.mgeologd),
                position: LatLng(lat, long),
                infoWindow: InfoWindow(
                  title: val.mcustno.toString(),
                  snippet: val.mlongname.toString(),
                ),
                icon: await MapsUtils.getMarkerIcon(
                    beanValForImageOnly, Size(150.0, 150.0)),
                onTap: () {
                  // _settingModalBottomSheet(context, val);
                  setState(() {}); //this is what you're looking for!
                });
            setState(() {
              markers.add(marker);
            });
          } catch (_) {}
        });
      } else {
        showMessageWithoutProgress("No Customers Found");
      }
    });

    //setState(() {});
  }

  void showMessage(String message) {
    try {
      _scaffoldHomeState.currentState.hideCurrentSnackBar();
    } catch (e) {}
    _scaffoldHomeState.currentState.showSnackBar(new SnackBar(
      backgroundColor: Colors.black,
      content: new Row(
        children: <Widget>[
          const CircularProgressIndicator(),
          new Text(message)
        ],
      ),
      duration: Duration(seconds: 300),
    ));
  }

  void showMessageWithoutProgress(String message) {
    try {
      _scaffoldHomeState.currentState.hideCurrentSnackBar();
    } catch (e) {}
    if (this.mounted == true) {
      _scaffoldHomeState.currentState.showSnackBar(new SnackBar(
          backgroundColor: Colors.black,
          duration: Duration(seconds: 5),
          content: Container(
              height: 30.0,
              child: new Column(children: <Widget>[new Text(message)]))));
    }
  }
}
